import { useContext } from "react";
import Context from "../Context";

export default function Favoritos() {
  const {fotos,setFotos}= useContext(Context);
  const fotosFavoritas = (fotos.filter((foto)=> foto.favorito)).length;
  
  return (
    <div>
      <h1>Fotos favoritas</h1>
      <div className="p-3 galeria grid-columns-4">
       {
         fotosFavoritas == 0?
         <p>No tienes fotos favoritas</p>:
         fotos
         .filter((foto)=>foto.favorito)
         .map((foto,i)=>(
           <div key={i}>
           <div
           onClick={() => setFavorito(foto.id)}
           className="foto"
           style={{backgroundImage: `url(${foto.src})`}}
           key={i}>
           <Heart filled={foto.favorito}/>
           <p>{foto.desc}</p>
         </div>
            </div>
          ))
       }
      </div>
    </div>
  );
}
